def findBasementPath():
    floor = 0
    position = 0
    positionFound = 0
    positionResult = 0
    for char in path:
        if (char == '('):
            floor += 1
        else:
            floor -= 1
        position += 1
        if (positionFound == 0 and floor < 0):
            positionFound = 1
            positionResult = position
    return { floor, positionResult }


